import React, { useEffect, useState } from 'react';
import axios from 'axios';

const MyGames = () => {
  const [myGames, setMyGames] = useState([]);

  useEffect(() => {
    // 從後端獲取使用者創建的球局
    const fetchMyGames = async () => {
      try {
        const response = await axios.get('/api/games/my-games'); // 假設有一個API能夠獲取創建者的球局
        setMyGames(response.data);
      } catch (error) {
        console.error('無法獲取我的球局', error);
      }
    };
    fetchMyGames();
  }, []);

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/games/${id}`);
      setMyGames(myGames.filter(game => game._id !== id)); // 刪除後更新狀態
      alert('球局已刪除');
    } catch (error) {
      console.error('刪除球局失敗', error);
      alert('刪除失敗');
    }
  };

  return (
    <div>
      <h1>我的球局</h1>
      {myGames.length === 0 ? (
        <p>你還沒有創建球局</p>
      ) : (
        <ul>
          {myGames.map((game) => (
            <li key={game._id}>
              <strong>日期:</strong> {new Date(game.date).toLocaleDateString()} <br />
              <strong>時間:</strong> {game.time} <br />
              <strong>球場:</strong> {game.course} <br />
              <strong>球資:</strong> {game.prices} <br />
              <strong>備註:</strong> {game.remarks || '無'} <br />
              <button onClick={() => handleDelete(game._id)}>刪除球局</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default MyGames;
